def partition(arr, first, last):
    wall = first
    for pos in range(first, last):
        if arr[pos] < arr[last]:
            (arr[pos], arr[wall]) = (arr[wall], arr[pos])
            wall += 1
    (arr[wall], arr[last]) = (arr[last], arr[wall])
    return wall
